import axios from "axios";
import { useParams } from "react-router";
import { useState } from "react/cjs/react.development";

export default function Trainings() {
  let params = useParams();
  let trainId = Number(params.trainingsId)
  const [aviation, setaviation]=useState("")
  const [technology, settechnology]=useState("")
  const [business, setbusiness]=useState("")

  function handleaviation(event){
    setaviation((event.target.value));
  }
  function handletechnology(event){
    settechnology((event.tatget.value));
  }
  function handlebusiness(event){
    setbusiness((event.target.value));
  }
  let ListPrograms = {
    aviation:aviation,
    technology:technology,
    business:business,
  }

  function handleClick(event){
    axios({
      method: "post",
      url: "",
      data: ListPrograms
    });
  }

  if(trainId == 7){
    return (
      // go to zoom 
      <main style={{ padding: "1rem 0" }}>
        <h2>trainings</h2>
        <h2>trainings</h2>
        <table border="1">
          <thead>
          <tr>
            <th colSpan="5"> First Week </th>
          </tr>
          </thead>
          <tbody>
          <tr>
            <td>Sunday</td>
            <td>Monday</td>
            <td>Tusday</td>
            <td>Wednesday</td>
            <td>Thursday</td>
          </tr>
          <tr>
            <td>9:00AM - 10:30AM <br/> Arabic Letters<br/> welcome men and women </td>
            <td>-</td>
            <td>10:00AM - 11:30AM <br/>Arabic number <br/>welcome men and women</td>
            <td>9:00AM - 11:00AM <br/> Most used words <br/> welcome men and women </td>
            <td>9:00AM - 11:00AM <br/> emergencey words <br/>welcome men and women</td>
          </tr>
          </tbody>
        </table>
          <br/> <br/> <br/> <br/>
        <table border="1">
          <thead>
          <tr>
            <th colSpan="5"> Second Week </th>
          </tr>
          </thead>
          <tbody>
          <tr>
            <td>Sunday</td>
            <td>Monday</td>
            <td>Tusday</td>
            <td>Wednesday</td>
            <td>Thursday</td>
          </tr>
          <tr>
            <td>9:00AM - 10:30AM <br/> Most used sentences<br/> welcome men and women </td>
            <td>-</td>
            <td>10:00AM - 11:30AM <br/> - - -  <br/>welcome men and women</td>
            <td>9:00AM - 11:00AM <br/>  - - - <br/> welcome men and women </td>
            <td>9:00AM - 11:00AM <br/>  - - - <br/>welcome men and women</td>
          </tr>
          </tbody>
        </table>


      </main>
    );
    }

    else if( trainId == 8){
      return(
        // go to database
        <main>
          <h1>Program areas</h1>
        <form>
          <input type="radio" id="aviation" value="aviation" onChange={handleaviation}/>
          <label>aviation field</label><br/>
          <input type="radio" id="technology" value="technology" onChange={handletechnology}/>
          <label>technology field</label><br/>
          <input type="radio" id="business" value="business" onChange={handlebusiness} />
          <label>business field</label>
          <button>submit</button>
</form>
        </main>
      )
    }}
