import { useParams } from "react-router";
import React,{useState} from "react";
import axios from "axios";

export default function Translators() {
    let params= useParams();
    let serviceId = Number(params.translatorsId)

    const[id, setid]= useState("");
    const[servicetype,setservicetype]=useState("");
    const[laoction,setlaoction]=useState("");
    const[nationalID,setnationalID]=useState("");
    const[date,setdate]=useState("");
    const[time,settime]=useState("");
    const[connectAs,setconnectAs]=useState("");
    const[email,setemail]=useState("");


    function handleid(event) {
        setid((event.target.value));
      }

      function handleservicetype(event) {
        setservicetype((event.target.value));
      }
      function handlelaoction(event) {
        setlaoction((event.target.value));
      }

      function handlenationalID(event) {
        setnationalID((event.target.value));
      }

      function handledate(event) {
        setdate((event.target.value));
      }
      function handletime(event) {
        settime((event.target.value));
      }

      function handleconnectAs(event) {
        setconnectAs((event.target.value));
      }

      function handleemail(event) {
        setemail((event.target.value));
      }

      let Listservices={
          id:id,
          servicetype:servicetype,
          laoction:laoction,
          nationalID:nationalID,
          date:date,
          time:time,
          connectAs:connectAs,
          email:email,
      }

      const[myServ,setMyServ]=useState({id:"",servicetype:"",laoction:"",nationalID:"",
        date:"",time:"",connectAs:"",email:""
    })

function handleClic(){
  axios({
      method:"post",
      url:"",
      date:Listservices
  });
}
    if(serviceId == 1){
        return(

            <main>


            <h2>Disaster distress Helpline</h2>

            <form>
                <input type="text" id="note" name="note" rows="10"  cols="30"/>

            </form>
            </main>
            );
    }
    else if(serviceId == 2){
        return(

            <main>


            <h2>Request for translaation of book</h2>

            <form>
            <form>
                    <label>National ID</label>
                    <input type="text" ></input><br />
                    <label>Service Type</label>
                    <input type="text"></input><br />
                </form>

                <label>email</label>
                <input type="text"></input><br />
                <input type="file"id="file"  name="file" rows="10" cols="30" />

            </form>
            </main>
            );
    }
    else if(serviceId == 3){
        return(

            <main>


            <h2>Request a face to face sign language translation</h2>

            <form>
            <form>
                    <label>National ID</label>
                    <input type="text"  onChange={handlenationalID} ></input><br />
                    <label>Service Type</label>
                    <input type="text" onChange={handleservicetype}></input><br />
                    <label>Location</label>
                    <input type="text" onChange={handlelaoction} ></input><br />
                </form>


                {/* <label>phonenumber</label>
                <input type="text" id="note" name="note" rows="10" cols="30" /><br />

                <label>gender</label><br />
                <input type="radio" value="male" /><label>male</label><br />
                <input type="radio" value="female" /><label>female</label><br />

                <label>email</label>
                <input type="text" id="note" name="note" rows="10" cols="30" /><br />

                <label>date</label><br />
                <input type="text" id="note" name="note" rows="10" cols="30" /><br />
                <label>location</label><br />
                <input type="text" id="note" name="note" rows="10" cols="30" /> */}


            </form>
            </main>
            );
    }

    else {
        return(

            <main>


            <h2>Request a sign lnguage translation online</h2>

            <form>
            <form>
                    <label>National ID</label>
                    <input type="text" onChange={handlenationalID}></input><br />
                    <label>Service Type</label>
                    <input type="text"></input><br />
                </form>

                {/* <label>phonenumber</label>
                <input type="text" id="note" name="note" rows="10" cols="30" /><br />

                <label>gender</label><br />
                <input type="radio" value="male" /><label>male</label><br />
                <input type="radio" value="female" /><label>female</label><br />

                <label>email</label>
                <input type="text" id="note" name="note" rows="10" cols="30" /><br /> */}

                <label>date</label>
                <input type="text" id="note" name="note" rows="10" cols="30" /><br />

                <label>Time</label>
                <input type="text" id="note" name="note" rows="10" cols="30" /><br />

                 <label>Connect As:</label><br />
                 <input type="radio" id="Zoom" value="Zoom" name="Connact_As"/> <label>Zoom</label><br />
                 <input type="radio" name="Connact_As"/><label>FaceTime</label><br />
                 <input type="radio" name="Connact_As"/><label>Teams</label><br />

            </form>
            </main>
            );
    }


  }
